mapper.update:
update news
set valid = false
where id = #{id}

mapper.select:
select news.id as id, news.title as title, news.sub_title as sub_title, news.create_time as create_time, news.last_edit_time as last_edit_time, news.content as content, news.news_tag_id as news_tag_id, news.hot as hot, news.img_list as img_list, news.notes as notes, news_tag.name as news_tag_name from news left join news_tag on news.news_tag_id = news_tag.id where news.valid = true
and news_tag_id = #{news_tag_id}
order by last_edit_time desc

select count(*) from news
where valid = true

    and news_tag_id = #{news_tag_id}
