mapper.select:
SELECT OFFICE.ID, OFFICE.NAME, OFFICE.BUILDING_ID, OFFICE.ADDRESS, OFFICE.TYPE_ID, OFFICE.AREA_VALUE, OFFICE.AREA_RANGE_ID, OFFICE.PRICE, OFFICE.PRICE_RANGE_ID, OFFICE.DECORATION_ID, ZONE.NAME AS ZONE_NAME, DISTRICT.NAME AS DISTRICT_NAME, TYPE.NAME AS TYPE_NAME, AREA_RANGE.NAME AS AREA_RANGE_NAME, PRICE_RANGE.NAME AS PRICE_RANGE_NAME, DECORATION.NAME AS DECORATION_NAME FROM OFFICE LEFT JOIN BUILDING ON OFFICE.BUILDING_ID = BUILDING.ID LEFT JOIN ZONE ON BUILDING.ZONE_ID = ZONE.ID LEFT JOIN DISTRICT ON ZONE.DISTRICT_ID = DISTRICT.ID LEFT JOIN TYPE ON OFFICE.TYPE_ID = TYPE.ID LEFT JOIN AREA_RANGE ON OFFICE.AREA_RANGE_ID = AREA_RANGE.ID LEFT JOIN PRICE_RANGE ON OFFICE.PRICE_RANGE_ID = PRICE_RANGE.ID LEFT JOIN DECORATION ON OFFICE.DECORATION_ID = DECORATION.ID
AND OFFICE.NAME LIKE #{keyword}
AND OFFICE.BUILDING_ID = #{building_id}
AND BUILDING.ZONE_ID = #{zone_id}
AND BUILDING.METRO_NAME_LIST LIKE #{metro_name}
AND OFFICE.TYPE_ID = #{type_id}
AND OFFICE.AREA_RANGE_ID = #{area_range_id}
AND OFFICE.PRICE_RANGE_ID = #{price_range_id}
AND OFFICE.DECORATION_ID = #{decoration_id}
ORDER BY OFFICE.ID DESC
LIMIT #{start}, #{step};

SELECT COUNT(*) FROM OFFICE


        AND OFFICE.NAME LIKE #{keyword}


        AND OFFICE.BUILDING_ID = #{building_id}


        AND OFFICE.ZONE_ID = #{zone_id}


        AND OFFICE.METRO_NAME_LIST LIKE #{metro_name}


        AND OFFICE.TYPE_ID = #{type_id}


        AND OFFICE.AREA_RANGE_ID = #{area_range_id}


        AND OFFICE.PRICE_RANGE_ID = #{price_range_id}


        AND OFFICE.DECORATION_ID = #{decoration_id}


;
